import React from "react";
import { Card } from "react-bootstrap";
//import Rating from "../Rating";

const Product = ({ duct }) => {
  return (
    <Card className="my-3 p-3 rounded">
      <a href={`/product/${duct._id}`}>
        <Card.Img src={product.image} variant="top" />
      </a>

      <Card.Body>
        <a href={`/product/${duct._id}`}>
          <Card.Title as="div">
            <strong>{product.name}</strong>
          </Card.Title>
        </a>

        {/* <Card.Text as="div">
          <Rating value={duct.rating} text={`${duct.numReviews} Reviews`} />
        </Card.Text> */}

        <Card.Text as="h3">${duct.price}</Card.Text>
      </Card.Body>
    </Card>
  );
};

export default Product;
